import java.util.*;

public class SimplicitePire {public static void main(String[] args) {String str = "Il fait beau aujourd'hui comme en aout";ArrayList<Character> ordre = new ArrayList<>(Arrays.asList('f', 'I', 'z', 'u', 'k', 'a', 'b', 'o'));String[] motus = triermot(str, ordre);nafmapuce(motus);}public static void nafmapuce(String[] youhou) {System.out.print("[");for (int j = 0; j < youhou.length - 1; j++) {System.out.print(youhou[j] + " , ");}System.out.print(youhou[youhou.length - 1] + "]");}public static String[] texttolist(String str) {str = str.replaceAll("[^a-zA-Z0-9]+", " ").trim();final String SEPARATEUR = " ";String mots[] = str.split(SEPARATEUR);return mots;}public static String[] triermot(String str, ArrayList<Character> ordre) {String mots[] = texttolist(str);String motus[] = new String[mots.length];int u = 0;for (int i = 0; i < ordre.size(); i++) {for (int j = 0; j < mots.length; j++) {char lettre1 = (mots[j].charAt(0));if (lettre1 == ((char) ordre.get(i))) {motus[u] = mots[j];u++;}}}for (int j = 0; j < mots.length; j++) {if (!ordre.contains(mots[j].charAt(0))) {motus[u] = mots[j];u++;}}return motus;}}
